import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { mergeMap, tap } from 'rxjs';
import { MoviesService } from 'src/app/services/movies.service';
import { ShowsService } from 'src/app/services/shows.service';

@Component({
  selector: 'app-article-details',
  templateUrl: './article-details.component.html',
  styleUrls: ['./article-details.component.sass'],
})
export class ArticleDetailsComponent implements OnInit {
  articleData = this.route.params.pipe(
    mergeMap((params) => {
      console.log(params);
      const { category, id } = params;
      return category === 'movies'
        ? this.moviesService.getMovie(id)
        : this.showsService.getShow(id);
    }),
    tap(console.log)
  );

  constructor(
    private route: ActivatedRoute,
    private moviesService: MoviesService,
    private showsService: ShowsService
  ) {}

  ngOnInit(): void {}
}
<div class="screen-container article-details-screen">

  <div class="article-details-screen__buttons">
    <custom-button icon="fa fa-arrow-left">Back</custom-button>
    <custom-button icon="fa fa-info-circle">More details</custom-button>
  </div>
  <div class="article-details-screen__card-container">
    <card></card>
  </div>

</div>

<custom-button></custom-button>

<div *ngIf="(articleData | async) as data; else notFound">
  <h1>{{data.original_title || data.original_name}}</h1>
</div>

<ng-template #notFound>
  <div>
    <h1>
      The article could not be found!
    </h1>
  </div>
</ng-template>
